import React, {useState} from 'react';
import housingdata from "../assets/housingdata.js";
import ClassList from "../container/ClassList";

const About = ({data}) => {
  // here is where we keep track of the todo list
  const [items,updateItems] = useState(data)

  // here is where we keep track of the values in the form
  const [name,setName] = useState("")
  const [address,setAdd] = useState("")
  const [price,setPrice] = useState("")
  const [leaseterm,setTime] = useState("")
  const [description, setDescription] = useState("")

  // this is the action when the submit button is pushed
  const addItem = (event) => {
    console.log('adding item ')
    console.dir(event)
    const item = {id:items.length, name:name, address:address,price:price,leaseterm:leaseterm,description:description, complete:false}
    updateItems(items.concat(item))
    document.getElementById('name').value = ""
    setName("")
    document.getElementById('address').value = ""
    setAdd("")
    document.getElementById('price').value = ""
    setPrice("")
    document.getElementById('leaseterm').value = ""
    setTime("")
    document.getElementById('description').value = ""
    setDescription("")

    event.preventDefault()
  }

  // these are called when the form elements are modified
  const updateName = event=> setName(event.target.value)
  const updateAddress = event=> setAdd(event.target.value)
  const updatePrice = event=> setPrice(event.target.value)
  const updateLeaseterm = event=> setTime(event.target.value)
  const updateDescription = event => setDescription(event.target.value)

  // handle the action when an item is clicked/completed
  let flipItem = item => (event) => {
    item.complete=!item.complete
    let newitems = items.map(x => (x.id===item.id?item:x))
    updateItems(newitems)
  }
      return (
        <div>
          <h1> Info of Available Houses </h1>
          <ul>
            {items
               .filter(item=>!item.complete)
               .map(item => (

              <li key={item.id}>
                <input type="checkbox"
                       onChange={flipItem(item)}
                       name={"complete"+item.id}
                       value={!item.complete} />
                {item.name} :{item.address}:{item.price}:{item.leaseterm}:{item.description}
              </li>

            ))}
          </ul>
          <form onSubmit={addItem}>
            <h2>New House For Rent</h2>
            name: <input type="text" id="name" name="name" onChange={updateName}/><br />
            address: <input type="text" id="address" name="address" onChange={updateAddress}/><br />
            price: <input type="text" id="price" name="price" onChange={updatePrice}/><br />
            leaseterm: <input type="text" id="leaseterm" name="leaseterm" onChange={updateLeaseterm}/><br />
            description: <input type="text" id="description" name="description" onChange={updateDescription} /><br />
            <input type="submit" value="submit!" />
          </form>
          <h2>Houses For Rent</h2>
          <p> This app show how to create a multiscreen application
          and to manipulate state using hooks.
          </p>
          <ClassList data={housingdata} /> //
        </div>
      )
}

export default About;
